$(window).on("load",()=>{var e=decodeURI(window.location.hash.replace("#","").replaceAll("+"," ")),t=$('a.image[title="'+e+'"]');t.length&&($("a").css("pointer-events","none"),setTimeout(()=>{$("html").animate({scrollTop:t.offset().top+(t.height()-$(window).height())/2},400,()=>{t.trigger("click"),$("a").css("pointer-events","")})},1500))});